@media (max-width: 768px) {
  .section-introduce .service-box {
    flex: 1 1 100%;
  }
}

/* Hiệu ứng fade-in xen kẽ */
.section-introduce .service-box.left {
  transform: translateX(-50px) scale(0.9);
}
.section-introduce .service-box.right {
  transform: translateX(50px) scale(0.9);
}

