.section-introduce .service-box.visible:nth-child(1) { transition-delay: 0.2s; }
.section-introduce .service-box.visible:nth-child(2) { transition-delay: 0.4s; }
.section-introduce .service-box.visible:nth-child(3) { transition-delay: 0.6s; }
.section-introduce .service-box.visible:nth-child(4) { transition-delay: 0.8s; }
.section-introduce .service-box.visible:nth-child(5) { transition-delay: 1s; }

/* Hover */
.section-introduce .service-box:hover {
  transform: scale(1.03) translateY(-6px);
  box-shadow: 0 8px 20px rgba(0,0,0,0.2), 0 0 12px rgba(49,61,93,0.3);
}

/* Title */
.section-introduce .service-box h2 {
  font-size: 20px;
  font-weight: bold;
  margin-bottom: 12px;
  text-align: center;
  background: linear-gradient(90deg, #313d5d, #4a5d9d);
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
}

/* Nội dung */
.section-introduce .service-box ul {
  list-style: disc;
  margin-left: 8px;
  line-height: 1.6;
}

.section-introduce .service-box li {
  margin-bottom: 8px;
