.section-introduce .service-box li:nth-child(2) { animation-delay: 0.4s; }
.section-introduce .service-box li:nth-child(3) { animation-delay: 0.6s; }
.section-introduce .service-box li:nth-child(4) { animation-delay: 0.8s; }

@keyframes fadeUp {
  from { opacity: 0; transform: translateY(10px); }
  to   { opacity: 1; transform: translateY(0); }
